static int
getInt2IntMap(int searchKey, size_t tableSize, const int *keys,
              const int *values, int defaultValue)
{
  int value = defaultValue;
  for (size_t i = 0; i < tableSize; ++i)
    if (keys[i] == searchKey)
      {
        value = values[i];
        break;
      }
  return value;
}


