{
  vlist_t *vlistptr = vlist_to_pointer(vlistID);

  vlistPtrCheckVarID(__func__, vlistptr, varID);

  vlistDecodeXyz(xyzStorVals[vlistptr->vars[varID].xyz], outDimorder);
}


